#include "Application.h"#include "Commands\Cone.h"#include "Commands\Cube.h"#include "Commands\Cylinder.h"#include "Commands\Sphere.h"#include "Commands\Split.h"#include "Commands\Torus.h"int main(int argc, char* argv[]) {	Application app;	app.registerCommand(std::make_unique<Sphere>());	app.registerCommand(std::make_unique<Cube>());	app.registerCommand(std::make_unique<Cone>());	app.registerCommand(std::make_unique<Cylinder>());	app.registerCommand(std::make_unique<Torus>());	app.registerCommand(std::make_unique<Split>());	return app.execute(argc, argv);}